<?phpglobal $pdo;// Résultats de la recherche via des cartes clicables qui renvoient à la fiche concernée$resultats = visualDataControverse($pdo);if(!empty($resultats)){    foreach ($resultats as $row) { ?>        <?php $id = $row['id_controverse'] ?>        <?php//Requête globale        $sql = 'SELECT * FROM controverse WHERE id_controverse=' . $id; // requete sql        $stmt = $pdo->prepare($sql); // préparation de la requete sql        $stmt->execute(); // execution avec affectation de la valeur :formInput        $result = $stmt->fetchAll(PDO::FETCH_ASSOC); // stockage en tableau des résultats?><!-- Les informations affichées sur la card (ici, le nom et la description de la controverse) -->        <div class="card mb-3">            <a style="text-decoration: none" href="./fiche?idControverse=<?= $id; ?>">                <div class="row g-0">                    <div class="col-md-8">                        <div class="card-body">                            <h5 class="card-title"><?= $result[0]['controverse_nom'] ?></h5>                            <h6> <?php if(isset($result[0]['controverse_description'])){ echo $result[0]['controverse_description'];} ?></h6>                        </div>                    </div>                </div>            </a>        </div><br>    <?php }} else {    ?>    <h4 style="text-align: center">Aucun résultat.</h4><?php }