<?phpglobal $pdo;// Résultats de la recherche via des cartes clicables qui renvoient à la fiche concernée$resultats = visualDataTaxon($pdo);if(!empty($resultats)){    foreach ($resultats as $row) { ?>        <?php $id = $row['id_ta_taxon_ancien'] ?>        <?php//Requête globale    $sql = 'SELECT ta_nom_ancien, ta_genre_bauhin_pinax, ta_espece, ta_image_url,ta_legende_image FROM ta_taxon_ancien WHERE id_ta_taxon_ancien=' . $id; // requete sql    $stmt = $pdo->prepare($sql); // préparation de la requete sql    $stmt->execute(); // execution avec affectation de la valeur :formInput    $result = $stmt->fetchAll(PDO::FETCH_ASSOC); // stockage en tableau des résultats    ?>        <!-- Les informations affichées sur la card (ici, le nom, le genre, l'espèce, l'image et sa légende) --><div class="card mb-3">    <a style="text-decoration: none" href="./fiche?idTaxon=<?= $id; ?>">        <div class="row g-0">            <div class="col-md-8">                <div class="card-body">                    <h5 class="card-title"><?= $result[0]['ta_nom_ancien'] ?></h5>                    <h6> <?php if(isset($result[0]['ta_genre_bauhin_pinax'])){ echo $result[0]['ta_genre_bauhin_pinax'];} if(isset($result[0]['ta_espece'])){ echo ' - '.$result[0]['ta_espece'];} ?></h6>                </div>            </div>            <div class="col-md-4">                <?php                if ($result[0]['ta_image_url']) { ?>                    <img src="<?= $result[0]['ta_image_url']; ?>" alt="<?= $result[0]['ta_legende_image']; ?>" class="img-fluid rounded" style="float: right; height:160px; width=auto; ">                <?php } ?>            </div>        </div>    </a></div><br>    <?php }} else {    ?>    <h4 style="text-align: center">Aucun résultat.</h4><?php }