<?phpglobal $pdo;// Résultats de la recherche via des cartes clicables qui renvoient à la fiche concernée$resultats = visualDataTextes($pdo);if(!empty($resultats)){foreach ($resultats as $row) { ?>    <?php $id = $row['id_texte'] ?>    <?php//Requête globale    $sql = 'SELECT texte_description, texte_titre FROM texte WHERE id_texte=' . $id; // requete sql    $stmt = $pdo->prepare($sql); // préparation de la requete sql    $stmt->execute(); // execution avec affectation de la valeur :formInput    $result = $stmt->fetchAll(PDO::FETCH_ASSOC); // stockage en tableau des résultats//Requête s'il y a des auteurs    $sqlAuteurs = 'SELECT pe_nom FROM texte    JOIN rel_texte_personne ON FK_id_texte = id_texte    JOIN pe_personne ON FK_id_pe_personne = id_pe_personne    WHERE id_texte=' . $id;    $stmtAuteurs = $pdo->prepare($sqlAuteurs); // préparation de la requete sql    $stmtAuteurs->execute(); // execution avec affectation de la valeur :formInput    $resultAuteurs = $stmtAuteurs->fetchAll(PDO::FETCH_ASSOC); // stockage en tableau des résultats    $titre = $result[0]['texte_titre'];    ?>    <!-- Les informations affichées sur la card (ici, le nom et la description du texte) -->    <div class="card">        <a style="text-decoration: none" href="./fiche?idTexte=<?= $id; ?>">            <div class="card-body">                <h5 class="card-title"><?= $titre; ?></h5>                <?php                if ($resultAuteurs) {                    if (count($resultAuteurs) == 1) { ?>                        <h6 class="card-subtitle mb-2 text-muted"><?= $resultAuteurs[0]['pe_nom'] ?></h6>                    <?php } else { ?>                        <h6 class="card-subtitle mb-2 text-muted">                            <?php foreach ($resultAuteurs as $row) {                                echo $row['pe_nom'] . ' ';                            } ?>                        </h6>                        <?php                    }                }                ?>                <?php if ($result) { ?>                    <p class="card-text"><?= $result[0]['texte_description'] ?></p>                <?php } ?>            </div>        </a>    </div><br><?php }} else {    ?>    <h4 style="text-align: center">Aucun résultat.</h4><?php }